mapper.select:
select id, user_name, sex, note from t_user
and user_name like concat('%', #{userName}, '%') and note like concat('%', #{note}, '%')
limit #{start}, #{limit}

mapper.update:
update t_user 

     user_name = #{userName},
     sex = #{sex, typeHandler=com.springboot.chapter11.typeHandler.SexTypeHandler},
    note = #{note}

where id= #{id}
